targets.rawSql:
SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(request_time) as "平均",host
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr}
GROUP BY time,host
ORDER BY time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps, host
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr}
	GROUP BY time,host
  order by time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(request_length) * 8 / ${cal_interval} as "今日最大请求量"
FROM ${project}
PREWHERE toDate(_timestamp) = today() and ${server_addr} and ${host}
GROUP BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) * 8 / ${cal_interval} as "今日最大返回量"
	FROM ${project}
	PREWHERE toDate(_timestamp) = today() and ${server_addr} and ${host}
GROUP BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS "今日最大QPS"
	FROM ${project}
	PREWHERE toDate(_timestamp) = today() and ${server_addr} and ${host}
	GROUP BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,countIf(status<400)/count() *100 as "请求成功率"
FROM ${project} 
PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host}
group by time
order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count()/${cal_interval} AS "当前QPS"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(request_time) as "整体响应时间"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host}
GROUP BY time
ORDER BY time

SELECT count()/10000 as "今日总PV(万)"
FROM ${project}
	PREWHERE toDate(_timestamp) = today() and ${server_addr} and ${host}

SELECT COUNT(DISTINCT remote_addr) AS "今日总UV"
FROM ${project}
PREWHERE toDate(_timestamp) = today() and ${server_addr} and ${host}

	SELECT toStartOfInterval(_timestamp, INTERVAL 60 SECOND) AS time, COUNT(DISTINCT remote_addr) AS "每分钟UV"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS "QPS"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.99)(request_time) as P99
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.90)(request_time) as P90
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.50)(request_time) as P50
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.75)(request_time) as P75
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.95)(request_time) as P95
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(request_time) as "平均"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

	SELECT
	  `uri` as TOP_Path, COUNT() AS PV, COUNT(DISTINCT remote_addr) as "UV",avg(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) as "平均返回"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host}
	GROUP BY `uri`
  order by COUNT() DESC

SELECT
  `uri` as TOP_Path,avg(request_time) as "指定状态响应时间"
FROM ${project}
PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
GROUP BY `uri`

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, remote_addr, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY time,remote_addr
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps, remote_addr
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time,remote_addr
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps, '整体'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(request_time) as rt,'整体'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,remote_addr,avg(request_time) as rt
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time,remote_addr
ORDER BY time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, server_addr, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time,server_addr
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, server_addr, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time,server_addr
  order by time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps,'整体'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(request_time) as rt, '整体'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,server_addr,avg(request_time) as rt
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time,server_addr
ORDER BY time

	SELECT toStartOfHour(_timestamp) AS time, count() AS "每小时PV"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfHour(_timestamp) AS time, COUNT(DISTINCT remote_addr) AS "每小时UV"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(request_length) * 8 / ${cal_interval} as "每秒请求量"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) * 8 / ${cal_interval} as "每秒返回量"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfMinute(_timestamp) AS time, countIf(request_time > 0.5) AS slow_requests
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host}
GROUP BY time
ORDER BY time

SELECT 
	host as "域名",avg(request_time) as "响应时间", count() as PV,COUNT(DISTINCT remote_addr) as UV
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host}
	GROUP BY host

SELECT
  uri as "请求路径", avg(request_time) as "平均响应时间",COUNT() AS PV, COUNT(DISTINCT remote_addr) as "UV",avg(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) as "平均返回量"
FROM ${project}
WHERE $__timeFilter(_timestamp) and ${server_addr} and ${host}
GROUP BY uri
HAVING PV >100
order by "平均响应时间" DESC
LIMIT 30;

SELECT
  uri as "请求路径",quantileExact(0.90)(request_time - upstream_response_time) as "P90NG处理时间",avg(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) as "平均返回量", avg(request_time) as "平均响应时间",COUNT() AS PV, COUNT(DISTINCT remote_addr) as "UV"
FROM ${project}
PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host}
GROUP BY `uri`
HAVING PV > 10
order by "P90NG处理时间" DESC
LIMIT 30;

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(request_time) as "平均",uri
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time,uri
ORDER BY time

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, uri, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time, uri
  order by time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,quantileExact(0.90)(request_time) as P90,uri
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time,uri
ORDER BY time

SELECT toStartOfMinute(_timestamp) AS time, uri,sum(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) as "返回量"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host}
GROUP BY time, uri
ORDER BY time

	SELECT
	  http_referer as Referer, 
    COUNT() as "PV", COUNT(DISTINCT remote_addr) as "UV"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY http_referer
  order by COUNT() DESC
	LIMIT 30;

	SELECT
	  `uri` as "请求路径", COUNT() AS PV, COUNT(DISTINCT remote_addr) as "UV",avg(request_time) as "平均响应时间",avg(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) as "平均返回量"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY `uri`
  order by PV DESC
	LIMIT 30;

	SELECT
	  `uri` as "请求路径",avg(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) as "平均返回量", avg(request_time) as "平均响应时间",COUNT() AS PV, COUNT(DISTINCT remote_addr) as "UV"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY `uri`
  order by "平均返回量" DESC
	LIMIT 30;

	SELECT
	  remote_addr as "用户IP", 
    COUNT() AS PV, COUNT(DISTINCT `uri`) as "请求URL数",sum(if(upstream_response_length = '', 0, toInt32(upstream_response_length))) as "接收数据量"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY remote_addr
  order by PV DESC
	LIMIT 30;

SELECT '' as "省份",'' as "城市",avg(request_time) as "响应时间",count() as "PV",COUNT(DISTINCT remote_addr) as UV
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
group by 1,2

	SELECT
	  client_region, 
    COUNT() AS "未解析"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY client_region

	SELECT
	  client_city, 
    COUNT() AS "未解析"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY client_city

	SELECT
	  client_os_family, 
    COUNT() AS "未解析"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host}
	GROUP BY client_os_family

	SELECT
	  client_browser_family, 
    COUNT() AS "未解析"
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
	GROUP BY client_browser_family
  order by COUNT() DESC
	LIMIT 10;

	SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time, client_city, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
	GROUP BY time,client_city
  order by time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(request_time) as "平均",client_city
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time,client_city
having count()/${cal_interval} > 1
ORDER BY time

SELECT toStartOfInterval(_timestamp, INTERVAL ${cal_interval} SECOND) AS time,quantileExact(0.90)(request_time) as P90,client_city
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_addr} and ${host} and ${status}
GROUP BY time,client_city
having count()/${cal_interval} > 1
ORDER BY time

SELECT COUNT() AS PV, client_latitude as latitude ,client_longitude as longitude,client_city as "城市",client_region as "省份"
FROM ${project}
PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
GROUP BY client_latitude,client_longitude,client_city,client_region

SELECT avg(request_time) as "响应时间", client_latitude as latitude ,client_longitude as longitude,client_city as "城市",client_region as "省份"
FROM ${project}
PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}
GROUP BY client_latitude,client_longitude,client_region,client_city

SELECT _timestamp, status, request_method, host,uri,query_string,server_addr,remote_addr,upstream_addr,upstream_response_time,request_length,if(upstream_response_length = '', 0, toInt32(upstream_response_length)), '' as client_city
	FROM ${project}
	PREWHERE $__timeFilter(_timestamp) and ${server_addr} and ${host} and ${status}

order by _timestamp desc
limit 500

